    public function addAction() {
//        $dzialy = $this->getDzialTable()->fetchAll();
//        $uzytkownicy = $this->getUzytkownikTable()->fetchAll();
        $role = $this->getRoleTable()->fetchAll();
        $users = $this->getUsersTable()->fetchAll();
        $resource = $this->getResourceTable()->fetchAll();
        $permission = $this->getPermissionTable()->fetchAll();
        
        $form = new RoleperForm($role, $resource, $permission);
        $form->get('submit')->setAttribute('value', 'Dodaj');

        $request = $this->getRequest();
        if ($request->isPost()) {
            $users = new Users();
            ////filter
//            $inputFilter = new TelefonFilter();
//            $form->setInputFilter($inputFilter);
            $form->setData($request->getPost());

            if ($form->isValid()) {
                $users->exchangeArray($form->getData());
                $this->getUsersTable()->saveUsers($users);
                return $this->redirect()->toRoute('admin');
            }
        }
        return array('form' => $form);
    }

    public function editAction() {
//        $dzialy = $this->getDzialTable()->fetchAll();
//        $uzytkownicy = $this->getUzytkownikTable()->fetchAll();
        $id = (int) $this->params('id');
        if (!$id) {
            return $this->redirect()->toRoute('admin', array('action' => 'add'));
        }
        $users = $this->getUsersTable()->getUsers($id);
        
        // var_dump($uzytkownik->stanowisko_id);
        // return 0;
        $form = new UsersForm();
        $form->bind($users);

        $form->get('submit')->setAttribute('value', 'Zapisz');

        $request = $this->getRequest();
        if ($request->isPost()) {
            $form->setData($request->getPost());

            if ($form->isValid()) {
                $this->getUsersTable()->saveUsers($users);
                //\Zend\Debug\Debug::dump($uzytkownik->nazwisko);
                //echo $uzytkownik->stanowisko_id;
                // die();
                ////Redirect to list of telefons
                return $this->redirect()->toRoute('admin');
            }
        }
        return array(
            'id' => $id,
            'form' => $form,
        );
    }

    public function deleteAction() {
        $id = (int) $this->params('id');

        if (!$id) {
            return $this->redirect()->toRoute('telefon');
        }

        $request = $this->getRequest();
        if ($request->isPost()) {
            $del = $request->getPost()->get('del', 'Nie');
            if ($del == 'Tak') {
                $id = (int) $request->getPost()->get('id');
                $this->getTelefonTable()->deleteTelefon($id);
            }

            ////Redirect to list of telefons
            return $this->redirect()->toRoute('telefon');
        }

        return array(
            'id' => $id,
            'telefon' => $this->getTelefonTable()->getTelefon($id)
        );
    }

